mapper.sql:

 		item item_,item_category item_category_


			,item_sku item_sku_

		where item_.cid=item_category_.cid


			and item_.item_id not in

                #{iid}



			and item_sku_.item_id = item_.item_id



				 and item_.item_name like CONCAT('%',#{entity.itemName},'%')


				 and FIND_IN_SET(item_.cid,(SELECT queryChildCategory(#{entity.cid}))) 


					 and item_.cid   in 

					 #{item} 




				 and item_.product_id  =#{entity.productId} 


				 and item_.item_status  =#{entity.itemStatus}


				 and item_.shop_id  in

	                #{shopId}



				 and item_.operator  =#{entity.operator} 



					and item_sku_.sku_id  =#{entity.skuId} 
					and item_sku_.sku_status=1



			    and item_.item_id=#{entity.id}


			    and item_.plat_link_status=#{entity.platLinkStatus}


			    and item_.has_price=#{entity.hasPrice}


			     AND item_.shop_cid=#{entity.shopCid}


			     AND item_.inventory >=#{entity.minInvetory} 


			     AND item_.inventory <=#{entity.maxInvetory}


			     AND item_.market_price>=#{entity.minPrice}


			     AND item_.market_price<=#{entity.maxPrice}


			     AND item_.plst_item_id=#{entity.platItemId}


			     AND item_.timing_listing >=#{entity.startTime}


			     AND item_.timing_listing <=#{entity.endTime}


			     AND item_.copied =#{entity.copied}


			     AND item_.item_status in

			    	#{status}



			     AND item_.brand in

			    	#{brand}



				and item_.shop_id IS NOT NULL



		      and item_.platform_id is null


		      and item_.platform_id =#{entity.platformId}



				and item_.shop_freight_template_id = #{entity.shopFreightTemplateId}


			     AND item_.add_source=#{entity.addSource}


				AND

					item_.placed_top = 1
					(item_.placed_top IS NULL OR item_.placed_top = 2)

mapper.update:
		update item set
   			status_change_reason=#{changeReason}
   			,item_status =#{itemStatus}
   			,modified = now()

	  		,delisting_time = now()

		    where shop_id=#{shopId}

	  		and item_status = 4

