	UPDATE item 
    SET ad = CASE item_id 

      WHEN #{ad.itemId} THEN #{ad.ad}

    END
	WHERE item_id IN 

		#{ad.itemId}

	UPDATE item 
    SET shop_cid = CASE item_id 

      WHEN #{cid.itemId} THEN #{cid.shopCid}

    END
	WHERE item_id IN 

		#{cid.itemId}

mapper.select:

select count(*)
from (
	select t.shop_id
	from item t 
	where t.plst_item_id=#{itemId}
	group by t.shop_id
) tt

mapper.update:
