select pm.sku_id, pm.item_id, pm.area_id, pm.min_num, pm.max_num, pm.sell_price, pm.promotion_price, pm.discount_percent, pm.markdown_type, p.id, p.seller_id, p.shop_id, p.activity_name, p.type , p.start_time, p.end_time, p.online_state, p.is_all_item, p.words, p.create_user, p.reviewer, p.create_time, p.update_time, p.platform_id, pf.meet_price, pf.discount_price, pf.sku_id pskuId, pf.item_id pitemId from promotion_info p LEFT JOIN promotion_full_reduction pf on pf.promotion_info_id=p.id LEFT JOIN promotion_markdown pm on pm.promotion_info_id=p.id where deleted = 0
and p.start_time > now() and p.online_state=1
and p.start_time <= now() and p.end_time >= now() and p.online_state=1
and p.end_time < now() and p.online_state=1
and p.start_time <= now() and p.end_time >= now() and p.online_state=3
and p.seller_id=#{entity.sellerId}
and (pm.max_num >=#{entity.num} and pm.min_num<=#{entity.num} ) or (pm.max_num is null and pm.min_num is null)
and (p.shop_id=#{entity.shopId} or p.shop_id=0)
and ((pf.item_id=#{entity.itemId} or pf.item_id=0) or (pm.item_id=#{entity.itemId} or pm.item_id=0))
and (pf.sku_id=#{entity.skuId} or pm.sku_id=#{entity.skuId})
and ( pm.area_id=#{entity.areaId} or pm.area_id is null)
and p.activity_Name like CONCAT('%',#{entity.activityName},'%')
and p.online_state=#{entity.onlineState}
and p.is_all_item=#{entity.isAllItem}
and p.type = #{entity.type}
and p.id = #{entity.promotionInfoId}
and p.platform_id = #{entity.platformId}
and (p.user_type = #{entity.userType} or p.user_type=0)
and p.user_type=0
and p.membership_level like CONCAT('%',#{entity.membershipLevel},'%')
and p.create_time > #{entity.createTimestr}
and p.create_time < #{entity.createTimeend}
ORDER BY p.shop_id
limit #{page.pageOffset} , #{page.rows}


