				and (pm.max_num >=#{entity.num} and pm.min_num<=#{entity.num} ) or (pm.max_num is null and pm.min_num is null) 	


				and p.seller_id=#{entity.sellerId}	


				and (p.shop_id=#{entity.shopId} or p.shop_id=0)	


